&lt;script&gt;
document.addEventListener(&#039;DOMContentLoaded&#039;, function () {
    const alertContainer = document.getElementById(&#039;alert-container&#039;);

    document.querySelectorAll(&#039;.agregar-carrito-form&#039;).forEach(form =&gt; {
        form.addEventListener(&#039;submit&#039;, function (e) {
            e.preventDefault();
            const id = this.dataset.id;
            const nombre = this.dataset.nombre;
            const cantidad = this.querySelector(&#039;input[name=&quot;cantidad&quot;]&#039;).value;

            fetch(&#039;http://localhost/RomeroFrancisco/catalogo/agregar&#039;, {
                method: &#039;POST&#039;,
                headers: {
                    &#039;Content-Type&#039;: &#039;application/json&#039;,
                    &#039;X-Requested-With&#039;: &#039;XMLHttpRequest&#039;
                },
                body: JSON.stringify({
                    id_producto: id,
                    cantidad: cantidad
                })
            })
                .then(res =&gt; res.json())
                .then(data =&gt; {
                    if (data.success) {
                        const alertHtml = `
                            &lt;div class=&quot;alert alert-success alert-dismissible fade show&quot; role=&quot;alert&quot;&gt;
                                Se agregó &lt;strong&gt;${cantidad}x &quot;${nombre}&quot;&lt;/strong&gt; al carrito correctamente.
                                &lt;button type=&quot;button&quot; class=&quot;btn-close&quot; data-bs-dismiss=&quot;alert&quot; aria-label=&quot;Cerrar&quot;&gt;&lt;/button&gt;
                            &lt;/div&gt;
                        `;
                        alertContainer.innerHTML = alertHtml;
                    } else {
                        alert(data.message || &#039;Error al agregar al carrito.&#039;);
                    }
                })
                .catch(error =&gt; {
                    alert(&#039;Error al conectar con el servidor.&#039;);
                    console.error(error);
                });
        });
    });
});
&lt;/script&gt;

